// import logo from './logo.svg';
import './App.css';
import React from 'react';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        complete:false,
      },
      editing:false,
    }

    this.fetchTasks = this.fetchTasks.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  };

  componentDidMount(){
    this.fetchTasks()
  }

  fetchTasks(){
    console.log('Fetching...')

    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(Response => Response.json())
    // .then(data=>console.log(data))
    .then(data =>
      this.setState({
        todoList:data
      })
      )
  }

  handleChange(e){
    var name = e.target.name
    var value = e.target.value
    console.log('Name:', name)
    console.log('Value:', value)

    this.setState({
      activeItem:{
        ...this.state.activeItem,
        title:value
      }
    })
  }

  handleSubmit(e){
    e.preventDefault();
    console.log('ITEM:', this.state.activeItem);
  }

  render(){
    var tasks = this.state.todoList

    return(
      <div className='container'>

        <div id='task-container'>

          <div id='form-wrapper'>
            <form onChange={this.handleSubmit} id='form'>
              <div className='flex-wrapper'>
                <div style={{flex: 6}}>
                  <input onChange={this.handleChange} className='form-control' id='title' type="text" name="title" placeholder='Add new task here' />
                </div>
                <div style={{flex: 1}}>
                  <input id='submit' className='btn btn-warpping' type="submit" name="Add" />
                </div>
              </div>
            </form>
          </div>

          <div id='list-wrapper'>
            {tasks.map(function(task, index){
              return(
                <div key={index} className="task-wrapper flex-wrapper">

                  <div style={{flex:7}}>
                    <span>{task.title}</span>
                  </div>

                  <div style={{flex:1}}>
                    <button className="btn btn-sm btn-outline-info">Edit</button>
                  </div>

                  <div style={{flex:1}}>
                    <button className="btn btn-sm btn-outline-dark delete">-</button>
                  </div>

                </div>
              )
            })}
          </div>

        </div>
      </div>
    )
  }
}

export default App;
